import {useState, useEffect} from "react";
import axios from 'axios';
import {useNavigate} from "react-router-dom";
import './PrivateScreen.css';


const PrivateScreen = () =>{

    console.log("PrivateScreen is rendered");

    const history = useNavigate();

    const [error, setError] = useState("");
    const [privateData, setPrivateData] = useState("");

    useEffect(() => {
        if(!localStorage.getItem("authToken")){
            history("/login");
        }

        const fetchPrivateData = async () => {

            const config = {
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${localStorage.getItem("authToken")}`
                }
            }

            try {
                const {data} = await axios.get("/api/private", config);
                setPrivateData(data.data);
            }catch (e) {
                localStorage.removeItem("authToken");
                setError("You are not authorized, please login");
            }
        }

        fetchPrivateData().then().catch(error => {
            console.log("Error:", error);
        });
    }, [history]);

    const logoutHandler = () => {
        localStorage.removeItem("authToken");
        history("/login");
    }

    return (
        <div className={`private-screen ${error ? "error" : ""}`}>
            <div className="private-screen__header">
                <h1>Email Verification</h1>
            </div>
            <div className="private-screen__content">
                {error ? (
                    <div>
                        <span className="error-message">{error}</span>
                    </div>
                ) : (
                    <div>
                        <p>{privateData}</p>
                    </div>
                )}
            </div>
            <div className="private-screen__footer">
                <button
                    onClick={logoutHandler}
                    className="private-screen__logout-button"
                >
                    Logout
                </button>
            </div>
            <button
                onClick={verificationHandler}
                className="private-screen__verification-button"
            >
                Verify
            </button>
        </div>
    );
};

export  default PrivateScreen;
